// Prepare context for smart question generation
// This runs once for all items
const items = $input.all();

// First item should be the webhook
const webhookItem = items[0];
const webhookBody = webhookItem.json.body || webhookItem.json;

console.log('[Smart Question Context] Processing items:', items.length);
console.log('[Smart Question Context] Webhook body received:', webhookBody);

const validationDetailId = webhookBody.validation_detail_id;
const validationResultId = webhookBody.validation_result_id;
const userGuidance = webhookBody.user_guidance || '';
const requirementText = webhookBody.requirement_text || '';
const existingSmartQuestion = webhookBody.existing_smart_question || '';

if (!validationDetailId) {
  console.error('[Smart Question Context] Full webhook data:', webhookItem.json);
  throw new Error('validation_detail_id not found in webhook body');
}

console.log('[Smart Question Context] IDs extracted:', {
  validationDetailId,
  validationResultId
});

// Fetch documents from previous node
const documentsNode = $('Fetch Validation Documents').all();
const documents = documentsNode && documentsNode.length > 0 
  ? documentsNode.map(item => item.json) 
  : [];

// Fetch prompt template from previous node
const promptNode = $('Fetch Smart Question Prompt').all();
const promptTemplate = promptNode && promptNode.length > 0 
  ? promptNode[0].json 
  : null;

// Build document context
let documentContext = '';
if (Array.isArray(documents) && documents.length > 0) {
  documentContext = `
**AVAILABLE DOCUMENTS** (${documents.length} files):
${documents.map((d, i) => `${i + 1}. ${d.file_name}`).join('\n')}
`;
} else {
  documentContext = '\n**NOTE**: No documents are available for this validation.';
}

// Build context string - FOCUSED ON REQUIREMENT
const contextString = `
**GENERATE SMART QUESTION FOR REQUIREMENT**
────────────────────────────────────────────────────────────────────

**REQUIREMENT TO ASSESS:**
${requirementText}

${existingSmartQuestion ? `**CURRENT QUESTION:**
${existingSmartQuestion}

**TASK:** Improve or replace this question.
` : '**TASK:** Generate a new question for this requirement.'}

${userGuidance ? `**USER GUIDANCE:**
${userGuidance}
` : ''}

**INSTRUCTIONS:**
1. Create ONE question that tests a learner's understanding of this requirement
2. Provide the benchmark answer - what a competent learner should write
3. The question should be clear, specific, and directly related to the requirement
4. The benchmark answer should be concise but complete

${documentContext}
`;

// Build full prompt
const systemPrompt = promptTemplate?.system_instruction || 
  'You are an expert assessment designer. Generate one SMART question and benchmark answer for a requirement.';

const promptText = promptTemplate?.prompt_text || 
  'Generate ONE smart question that tests understanding of this requirement, with the benchmark answer.';

const fullPrompt = `${contextString}

${promptText}`;

console.log('[Smart Question Context] Context prepared successfully');

// Return array with single item for "Run Once for All Items" mode
return [{
  json: {
    validation_detail_id: validationDetailId,
    validation_result_id: validationResultId,
    system_prompt: systemPrompt,
    full_prompt: fullPrompt,
    documents: documents,
    prompt_template: promptTemplate,
    user_guidance: userGuidance,
    requirement_text: requirementText,
    existing_smart_question: existingSmartQuestion
  }
}];

// Build Gemini request for smart question generation
const context = $input.item.json;
const documents = Array.isArray(context.documents) 
  ? context.documents 
  : (context.documents ? [context.documents] : []);

// Build file parts from documents
const fileParts = documents
  .filter(doc => doc.gemini_file_uri)
  .map(doc => ({
    fileData: {
      mimeType: doc.mime_type || 'application/pdf',
      fileUri: doc.gemini_file_uri
    }
  }));

// Build user prompt
const userPrompt = `Based on the attached documents, generate 5-10 intelligent, contextually relevant questions that help users discover and understand critical information within the validation context.

Each question should:
- Be answerable from the provided documents
- Be relevant to validation assessment and evidence
- Have a clear question type (factual, analytical, comparative, inferential, or application)
- Include a difficulty level (basic, intermediate, or advanced)
- Specify the focus area (assessment_criteria, evidence, compliance, structure, or findings)
- List expected document sections where answers can be found
- Include a rationale explaining why the question is important

Return the response as a JSON object with a 'questions' array containing the generated questions.`;

// Build Gemini request
const geminiRequest = {
  systemInstruction: {
    parts: [{
      text: context.system_prompt
    }]
  },
  contents: [
    {
      role: 'user',
      parts: [
        ...fileParts,
        {
          text: userPrompt
        }
      ]
    }
  ],
generationConfig: {
  temperature: 0.8,
  maxOutputTokens: 2048,  // ✅ Reduced from 4096 to prevent truncation
  topP: 0.95,
  topK: 40,
  responseMimeType: "application/json",
  responseSchema: context.prompt_template.output_schema
}
};

return {
  json: {
    validation_detail_id: context.validation_detail_id,
    documents_available: fileParts.length > 0,
    document_count: fileParts.length,
    gemini_request: geminiRequest
  }
};

// Extract response from Gemini
const geminiResponse = $json.candidates?.[0]?.content?.parts?.[0]?.text;

if (!geminiResponse) {
  throw new Error('No response received from Gemini API');
}

// Check if response was truncated
const finishReason = $json.candidates?.[0]?.finishReason;
if (finishReason === 'MAX_TOKENS') {
  console.warn('[Extract Smart Question] Response was truncated due to max tokens!');
}

// Parse JSON response - NEW STRUCTURE: { question, benchmark_answer }
let parsedResponse;
try {
  // Try to parse the response
  parsedResponse = typeof geminiResponse === 'string' ? JSON.parse(geminiResponse) : geminiResponse;
  console.log('[Extract Smart Question] Parsed response:', parsedResponse);
} catch (e) {
  console.error('[Extract Smart Question] Failed to parse Gemini response:', e);
  console.error('[Extract Smart Question] Raw response:', geminiResponse.substring(0, 500));

  // Fallback to empty response
  parsedResponse = { 
    question: 'Failed to generate question - please try again',
    benchmark_answer: 'No answer generated'
  };
}

// Get context from previous node
const contextNode = $('Prepare Smart Question Context').first();
const validationDetailId = contextNode.json.validation_detail_id;
const validationResultId = contextNode.json.validation_result_id;

// Validate we got a question and answer
if (!parsedResponse.question || !parsedResponse.benchmark_answer) {
  console.error('[Extract Smart Question] Missing required fields:', parsedResponse);
  throw new Error('Invalid response from Gemini - missing question or benchmark_answer');
}

console.log('[Extract Smart Question] Successfully extracted:', {
  validationDetailId,
  validationResultId,
  hasQuestion: !!parsedResponse.question,
  hasBenchmark: !!parsedResponse.benchmark_answer,
  wasTruncated: finishReason === 'MAX_TOKENS'
});

// Wrap in array format for frontend compatibility
// Frontend expects: { questions: [{ question, rationale }] }
return {
  json: {
    validation_detail_id: validationDetailId,
    validation_result_id: validationResultId,
    questions: [{
      question: parsedResponse.question,
      rationale: parsedResponse.benchmark_answer,  // Map benchmark_answer to rationale for frontend
      question_type: 'smart_question',
      difficulty_level: 'intermediate',
      focus_area: 'requirement_understanding'
    }],
    summary: 'Smart question generated for requirement',
