parameters.jsCode:
const input = $input.first().json;
const extractedPoints = input.output?.extractedPoints || [];
const results = [];

for (const point of extractedPoints) {
  let element = '';
  let elementTitle = '';
  let pcNumber = '';
  let pcText = '';

  for (const [key, value] of Object.entries(point)) {
    if (key.startsWith('Element')) {
      element = key.replace('Element ', '');
      elementTitle = value;
    } else if (key.startsWith('Performance Criterion')) {
      pcNumber = key.replace('Performance Criterion ', '');
      pcText = value;
    }
  }

  if (element && pcNumber) {
    results.push({
      json: {
        id: `PC ${pcNumber}`,
        element: element,
        elementTitle: elementTitle,
        pcNumber: pcNumber,
        text: pcText
      }
    });
  }
}

return results;

