const item = $input.first().json;
let fsText = item.fs || '';

// Check for bad/misplaced data first
const badDataIndicators = [
  'Unit sector',
  'Competency field',
  'Pre-requisite unit'
];

const isBadData = badDataIndicators.some(indicator => 
  fsText.startsWith(indicator) || 
  (fsText.includes(indicator) && !fsText.includes('Skill'))
);

if (isBadData) {
  // Try to extract from UOCpage
  // ... (same as before)
}

// Phrases indicating implicit FS
const implicitPhrases = [
  'explicit in the performance criteria',
  'embedded in the performance criteria',
  'incorporated in the performance criteria'
];

// Check if it's implicit - BUT only if there's NO table
const hasTable = fsText.includes('| Skill') || 
                 fsText.includes('|Skill') ||
                 fsText.includes('| **Skill**') ||
                 fsText.includes('| Reading') ||
                 fsText.includes('| Writing');

const hasImplicitPhrase = implicitPhrases.some(phrase => 
  fsText.toLowerCase().includes(phrase.toLowerCase())
);

// It's implicit ONLY if it has the implicit phrase AND NO table
const isImplicit = hasImplicitPhrase && !hasTable;

if (isImplicit) {
  return {
    json: {
      isImplicit: true,
      extractedPoints: [{
        id: "FS",
        text: "Foundation skills are implicit in the performance criteria of this unit.",
        type: "implicit"
      }],
      originalText: fsText,
      unitCode: item.unitCode
    }
  };
} else {
  // Has explicit FS table - send to LLM
  return {
    json: {
      isImplicit: false,
      foundationSkillsText: fsText,
      unitCode: item.unitCode,
      unitTitle: item.Title
    }
  };
}

